<template>
<div class="panel-header-index">  
	<el-row>	
		<el-col :span="6">
			<slot name="logo">
			 	<a href="https://gitee.com/jjxliu306/ng-form-element" target="_black">
					<img class="logo" src="data:image/png;base64," >
					<span class="title">ng-form-element</span>
				</a>
			</slot>
		</el-col>
		<el-col :span="8" style="min-height: 30px;">
			<slot name="formName"></slot>
		</el-col>
		<el-col :span="10"> 
			<span class="buttons">
				<slot name="controlButton"></slot>
				<el-button v-if="clear" type="text" size="medium" icon="el-icon-delete" @click="handleClear">{{t('ngform.header.clear')}}</el-button>
			    <el-button v-if="preview" type="text" size="medium" icon="el-icon-view" @click="handlePreview">{{t('ngform.header.preview')}}</el-button>
			  	<!--   <el-button v-if="reder"  type="text" size="medium" icon="el-icon-view" @click="handleRender">渲染</el-button> -->
			    <el-button v-if="imp" type="text" size="medium" icon="el-icon-download" @click="handleImport">{{t('ngform.header.imp')}}</el-button>
			    <el-button v-if="exp" type="text" size="medium" icon="el-icon-upload2" @click="handleExport">{{t('ngform.header.exp')}}</el-button>
			</span> 
		</el-col> 
	</el-row>	 

  <Preview v-if="previewVisible" ref="preview" />
  <Code v-if="codeVisible" ref="code" @imp="importData" />
</div>
</template>
<script>
import Preview from './preview.vue'
import Code from './code.vue'
import LocalMixin from '../../locale/mixin.js'
export default{
	mixins: [LocalMixin],
	components: {
		Preview,
		Code
	},
	data() {
		return {
			previewVisible: false,
			codeVisible: false
		}
	},
	props: {
		formTemplate: {
			type: Object
		},
		// 按钮显示隐藏 
	  clear: {
	    type: Boolean ,
	    default: true
	  },
	  preview: {
	    type: Boolean ,
	    default: true
	  },
	  reder: {
	    type: Boolean ,
	    default: true
	  },
	  imp: {
	    type: Boolean ,
	    default: true
	  },
	  exp: {
	    type: Boolean ,
	    default: true
	  }
	},
	methods: {
		handleClear() {
			this.$confirm( this.t('ngform.header.clear_prompt'),   this.t('ngform.header.prompt'), {
          		confirmButtonText:  this.t('ngform.confirm') ,
          		cancelButtonText:  this.t('ngform.cancel') ,
          		type: 'warning'
      		}).then(() => {
        		this.$set(this.formTemplate , 'list' , [])
      		})
			
		},
		handlePreview() {
			this.previewVisible = true 
			this.$nextTick(()=> {
				this.$refs.preview.init(this.formTemplate)
			})
		},
		handleImport() {
			this.codeVisible = true 
			this.$nextTick(()=> {
				this.$refs.code.init(this.formTemplate , true)
			})
		},
		// 外部导入
		importData(codes) {
			this.$emit('importData' , codes)
		},
		handleExport() {
			this.codeVisible = true 
			this.$nextTick(()=> {
				this.$refs.code.init(this.formTemplate)
			})
		}
	}
}
</script>
<style>
.panel-header-index {
  height: 100%;
}

.panel-header-index .logo, .panel-header-index .title {
  float: left;
}

.panel-header-index .title {
  line-height: 40px;
  height: 40px;
  padding-left: 20px;
}

.panel-header-index .buttons {
  float: right;
}

</style>
